False Color
evalscript = """
//VERSION=3
function setup() {
  return {
    input: ["S3", "S2", "S1"],
    output: {
      bands: 3,
      sampleType: "AUTO",
    },
  }
}

function evaluatePixel(sample) {
  return [2 * sample.S3, 2 * sample.S2, 2 * sample.S1]
}
"""

request = {
    "input": {
        "bounds": {
            "bbox": [
                8.558382,
                41.359678,
                9.579525,
                43.055688,
            ],
            "properties": {"crs": "http://www.opengis.net/def/crs/EPSG/0/4326"},
        },
        "data": [
            {
                "type": "sentinel-3-slstr",
