evalscript = """
//VERSION=3
function setup() {
  return {
    input: [
      {
        datasource: "s2l1c",
        bands: ["B02", "B03", "B04"],
      },
      {
        datasource: "s3olci",
        bands: ["B04", "B06", "B08"],
      },
    ],
    output: [
      {
        bands: 3,
      },
    ],
  }
}

function evaluatePixel(
  samples,
  inputData,
  inputMetadata,
  customData,
  outputMetadata
) {
  let s3 = samples.s3olci[0]
  let s2 = samples.s2l1c[0]
  let amount_s2 = 0.5
  let gain = 3.0
  return [
    gain * (s3.B08 * (1 - amount_s2) + s2.B04 * amount_s2),
    gain * (s3.B06 * (1 - amount_s2) + s2.B03 * amount_s2),
    gain * (s3.B04 * (1 - amount_s2) + s2.B02 * amount_s2),
  ]
}
"""

